using Itc4net;  namespace Core.API.IntervalTreeClocks {     public class ItcProcess     {         readonly string name;         private Stamp stamp;          public ItcProcess(string name, Stamp stamp)         {             this.name = name;             this.stamp = stamp;         }          public Stamp CurrentStamp => stamp.Peek();          public ItcMessage Send(string text)         {             stamp = stamp.Send(out Stamp anonymous);             return new ItcMessage(text, anonymous);         }          public Stamp Receive(ItcMessage itcMessage)         {             stamp = stamp.Receive(itcMessage.Stamp);             return CurrentStamp;         }          public Stamp Increment()         {             stamp = stamp.Event();             return CurrentStamp;         }     } } 